//  MW WORLD.

world = new MW.World();

//  OCTREE.

//  Make a octree object, which will be the 
//  container of rigid objects such as terrain ect.

var partition = 2;

var min = new THREE.Vector3( -1000, -1000, -1000 );
var max = new THREE.Vector3(  1000,  1000,  1000 );

octree = new MW.Octree( min, max, partition ); // important!

world.add( octree );

//  GROUND.

ground = new THREE.Mesh(
	new THREE.PlaneGeometry( 2500, 2500, 1, 1 ),
	new THREE.MeshLambertMaterial({ 
		color:0x829ec4,
		wireframe:false,
	})
);

ground.rotation.x = THREE.Math.degToRad( -90 );

//  If want the ground as collision surface
//  only, do not add the ground in the scene.

//	scene.add( ground );
//	ground.visible = false;
//	ground.receiveShadow = true;

//  Add the ground to WALKMESH world octree object.
//  MESHWALK use octree system to detect collision objects.

octree.importThreeMesh( ground ); // important!

//	Runtime.

var clock = new THREE.Clock(); // important!

function update(){

	var dt = clock.getDelta(); // important!
	var time = clock.getElapsedTime(); // optional!

	world.step( dt ); // important!

	THREE.AnimationHandler.update( dt ); // important!
}

//  PlayerController.js

var Player = function(radius){

	this.holder = AW3D.PlayerHolder(); // important!
	scene.add( this.holder );
	this.holder.visible = true;

	this.holderHelper = AW3D.PlayerHolderHelper();
	scene.add( this.holderHelper );

	this.sphere = AW3D.PlayerSphere();
	this.sphere.position.y = 12;
	this.holder.add( this.sphere ); 

	var player = this; // important!

	this.outfit = new AW3D.OutfitManager(); // important!

	this.outfit.update = function() {

	//  Update avatar rotation y.
		var direction = player.controller.direction - Math.PI;

	//  "this" is the "localPlayer.outfit".
		this.direction.rotation.y = direction;

	//  Update avatar position.
		var x = player.controller.center.x;
		var y = player.controller.center.y - player.controller.radius;
		var z = player.controller.center.z;

	//  "this" is the "localPlayer.outfit".
		this.direction.position.set( x, y, z );
	};

	var radius = radius || 3;
	this.controller = new MW.CharacterController( this.holder, radius );
	this.controller.isWalking = false;

	this.controller.getdata = function( action ){

		var data = {};
		data.playerid = socket.id;
		data.radius = this.radius;
		data.isGrounded = this.isGrounded;
		data.isOnSlope = this.isOnSlope;
		data.isIdling  = this.isIdling;
		data.isJumping = this.isJumping;
		data.isRunning = this.isRunning;
		data.isWalking = this.isWalking;
		data.direction = this.direction;
		data.movementSpeed = this.movementSpeed;
		data.jumpStartTime = this.jumpStartTime;
		data.position = this.center.toArray();
		if (!!action) data.action = action;

		return data;
	};

	function onStartJumping(){
		player.outfit.AnimationsHandler.weightOff("idle", "walk", "run");
	};

	function onEndJumping(){
		player.outfit.AnimationsHandler.weightOn("idle", "walk", "run");

		if ( player.controller.isRunning && player.controller.isWalking ) {

			player.outfit.AnimationsHandler.stop();
			player.controller.movementSpeed = 28;
			player.outfit.AnimationsHandler.play("walk");
			player.controller.dispatchEvent({type:"startRunning"});

		} else if ( player.controller.isRunning && !player.controller.isWalking ) {

			player.outfit.AnimationsHandler.stop();
			player.controller.movementSpeed = 45;
			player.outfit.AnimationsHandler.play("run");
			player.controller.dispatchEvent({type:"startRunning"});

		} else {

			player.outfit.AnimationsHandler.stop();
			player.controller.movementSpeed = 0;
			player.outfit.AnimationsHandler.play("idle");
			player.controller.dispatchEvent({type:"startIdling"});

		}
	}

	this.controller.addEventListener("endJumping",   onEndJumping);
	this.controller.addEventListener("startJumping", onStartJumping);

//  Add controller to world.
	world.add( this.controller ); // important!

	debugMode && console.log( 
		"world.characterPool length:", world.characterPool.length, 
		"index:", world.characterPool.indexOf( this.controller )
	);

//  Add outfit to scene.
	scene.add(this.outfit.direction); // important!

};

Player.prototype = {

//  Collect position, direction, nickname, and gender.

	getdata: function( socket ){
		if ( !socket ) return;
		var data = {};
		data.playerid  = socket.id;
		data.nickname  = this.nickname;
		data.gender    = this.outfit.getGender();
		data.direction = this.outfit.direction.rotation.y;
		data.position  = this.outfit.direction.position.toArray();
		data.dna       = this.outfit.toDNA();
		debugMode && console.log("player data:", data);
		return data;
	},

//  Remote player set data.

	setdata: function( data ){
		if ( !data.playerid || data.playerid != this.playerid ) return;

		this.playerid = data.playerid;
		this.nickname = data.nickname;
		this.outfit.setGender( data.gender );
		this.outfit.direction.rotation.y = data.direction;
		this.outfit.direction.position.fromArray(data.position);

		if (data.action != undefined) {
			this.outfit.AnimationsHandler.stop();
			this.outfit.AnimationsHandler.play( data.action );
		}
	},

//  Create player outfit from dna data.

	fromDNA: function( dna ){
		var frontAngle = Math.PI - cameraControls.getFrontAngle(); // face front.
		this.controller.direction = frontAngle;
		this.outfit.fromDNA( dna );
		scene.add(this.outfit.direction);
		this.outfit.update();
	},

//  Start remote player action.
	startAction: function( running, walking, idling, speed, name, eventType ){

		this.outfit.AnimationsHandler.stop();        
		this.controller.isRunning = running;
		this.controller.isWalking = walking;
		this.controller.isIdling  = idling;
		this.controller.movementSpeed = speed;
		this.outfit.AnimationsHandler.play( name );
		this.controller.dispatchEvent({type: eventType});

	},

//  Get local player action data.
	getActionData: function ( name ){
		return {
			playerid : socket.id,
			direction: this.outfit.direction.rotation.y,
			position : this.outfit.direction.position.toArray(),
			action   : name,
		};
	},

};

function init(){

	localPlayer = new Player(); // important!
	localPlayer.sphere.visible = false;
	debugMode && console.log("localPlayer:", localPlayer);

//  cameraControl.js

	cameraControls = new MW.TPSCameraControl(camera, localPlayer.holder, {
		el: renderer.domElement,               // html renderer element.
		offset: new THREE.Vector3( 0, 15, 0 ), // camera eye height.
		radius: 40,                            // default: 37 // (distance of the character to the camera).
		minRadius: 6,                          // default: 10 // (can take and negative values, yes!!!).
		maxRadius: 1500,                       // default: 64, runtime: 40.
		rigidObjects: [],                      // collition objects for the camera.
	});

	cameraControls.getforward = function(){ 
		return -this.theta 
	};

	cameraControls.setVerticalOffset = function(offset){ 
		this.offset.y = offset; 
	};

	(cameraControls.frontAngleUpdate = function(){
		requestAnimationFrame( cameraControls.frontAngleUpdate );
		cameraControls.forward = -cameraControls.theta;
	})();

/*
	cameraControls.addEventListener("updated", function(){
		defaultCameraLight.position.copy( cameraControls.camera.position );
	});
*/

//  KeyInputControls.js

	keyInputControls = new MW.KeyInputControl();

	keyInputControls.On = function(){
		if ( !keyInputControls.isOff ) return;
		$(keyInputControls).on( "movekeyon", onMoveKeyOn );
		$(keyInputControls).on( "movekeyoff", onMoveKeyOff );
		$(keyInputControls).on( "jumpkeypress", onJumpInput );
		$(keyInputControls).on( "movekeychange", onMoveKeyChange );
		keyInputControls.isOff = false;
		debugMode && console.log( "keyInputControls are ON." );
	};

	keyInputControls.Off = function(){
		if ( keyInputControls.isOff ) return;
		$(keyInputControls).off( "movekeyon", onMoveKeyOn );
		$(keyInputControls).off( "movekeyoff", onMoveKeyOff );
		$(keyInputControls).off( "jumpkeypress", onJumpInput );
		$(keyInputControls).off( "movekeychange", onMoveKeyChange );
		keyInputControls.isOff = true;
		debugMode && console.log( "keyInputControls are OFF." );
	};

	keyInputControls.update = function(){

		if ( keyInputControls.isOff ) return;

		if ( keyInputControls.isMoveKeyHolded 
			|| localPlayer.controller.isJumping 
			|| !localPlayer.controller.isGrounded 
			|| localPlayer.controller.isOnSlope ){

			this.dispatchEvent({type:"update"});
		}
	};

//	Activate keyInputControls by setting to "off".
	keyInputControls.Off(); // important!
//	keyInputControls.inRun = true;

	function onMoveKeyOn() { 
		debugMode && console.log("move key on");

		if (  localPlayer.controller.isJumping 
			|| !localPlayer.controller.isGrounded 
			||  localPlayer.controller.isOnSlope ) {
			return;
		}

		localPlayer.controller.direction = (2 * Math.PI) - cameraControls.getFrontAngle() + this.frontAngle;

		localPlayer.outfit.AnimationsHandler.stop();
		localPlayer.controller.isRunning = true; 
		localPlayer.controller.isWalking = true;
		localPlayer.controller.movementSpeed = 28;
		localPlayer.outfit.AnimationsHandler.play("walk");

	}

	function onMoveKeyChange() {
		debugMode && console.log("move key changed");
		localPlayer.controller.direction = (2 * Math.PI) - cameraControls.getFrontAngle() + this.frontAngle;
	}

	function onMoveKeyOff() {
		debugMode && console.log("move key off");

		if (  localPlayer.controller.isJumping 
			|| !localPlayer.controller.isGrounded 
			||  localPlayer.controller.isOnSlope ) {
			return;
		}

		var finalDirection = (2 * Math.PI) - cameraControls.getFrontAngle() + this.frontAngle;

		localPlayer.controller.direction = finalDirection;
		localPlayer.outfit.AnimationsHandler.stop();
		localPlayer.controller.isRunning = false;
		localPlayer.controller.isWalking = false;
		localPlayer.controller.isIdling  = true;
		localPlayer.controller.movementSpeed = 0;
		localPlayer.outfit.AnimationsHandler.play("idle");
		localPlayer.controller.dispatchEvent({type:"startIdling"});

	}

	function onJumpInput() {
		debugMode && console.log("jump key on");

		if (  localPlayer.controller.isJumping 
			|| !localPlayer.controller.isGrounded 
			||  localPlayer.controller.isOnSlope ) {
			return;
		}

		localPlayer.controller.jump();
		localPlayer.outfit.AnimationsHandler.jump();

	}

//  JoystickControls.js (requires "jquery.js")

	$(document.head).append(
		"<style class='joystick-controls'> \n.gameinput-button__inner { display:table-cell; vertical-align: middle; } \n"
