debugMode && console.log(this);

var helper = this;
var fontPath = "/three/fonts/helvetiker_regular.typeface.json";

groundHelper = groundHelper(1000, 10);  
axisCustomHelper = customAxisHelper(5200);
axisOriginHelper = originAxisHelper(1200);

function init(){
