                        resolve( this.fromJSON(json) );
                    }).catch( function(err){ 
                        console.error(err);
                        throw err; 
                    });

                } else if ( validator.isJSON( dna ) ) {

                    return new Promise( (resolve, reject) => {
                        var json = JSON.parse( dna );
                        resolve( this.fromJSON(json) );
                    }).catch( function(err){ 
