//	basic-animation.js

Animations = {}; // object.

var loader = new THREE.XHRLoader();
loader.setResponseType( "json" );
loader.load("/AW3D_db/animations.json", function(results){

	//  returns an array of objects!

	debugMode && console.log("animations:", results);

	var json = results.find(function(item){
		return item._id == "basic";
	});

	debugMode && console.log("basic:", json);

	for (var name in json){
